import { useSelector, useDispatch } from 'react-redux'
import { createStore } from 'redux'
import { anecdoteReducer, toAddVote, asObject } from './reducers/anecdoteReducer'

const App = () => {
  const anecdotes = useSelector(state => state.sort((a,b) => b.votes-a.votes))
  //console.log(anecdotes, 'is anecdotes after selector')
  const dispatch = useDispatch()
  //const store = createStore(anecdoteReducer)

  const vote = (id) => {
    dispatch(toAddVote(id))
  }
  
  const newPhrase = (event) => {
    event.preventDefault()
    const content = event.target.anecdote.value
    event.target.anecdote.value = ''
    console.log('we got here first')
    dispatch(asObject(content))
  }
  	
  

  return (
    <div>
      <h2>Anecdotes</h2>
      {anecdotes.map(anecdote =>
        <div key={anecdote.id}>
          <div>
            {anecdote.content}
          </div>
          <div>
            has {anecdote.votes}
            <button onClick={() => vote(anecdote.id)}>vote</button>
          </div>
        </div>
      )}
      <AnecdoteForm />
    </div>
  )
}

export default App
