import { useSelector, useDispatch } from 'react-redux'
import { toAddVote } from '../reducers/anecdoteReducer'

const Anecdotes = () => {

  const anecdotes = useSelector(state => state)
  //const anecdotes = useSelector(state => state.sort((a,b) => b.anecdotes.votes - a.anecdotes.votes))
  //console.log(anecdotes, 'is anecdotes')
  //const anecdotesWords = anecdotes.anecdotes
  //console.log(anecdotesWords, 'is anec words')
  //const aTest = useSelector(anecdotes => anecdotes.sort((a,b) => b.votes-a.votes))
  
  
  console.log(anecdotes, 'is anecdotes')
  console.log(anecdotes.anecdotes, 'is anecdotes squared')
  console.log(anecdotes.anecdotes[0], 'is anecdotes squared 0')
  console.log(anecdotes.anecdotes[0].content, 'is anecdotes squared 0 content')
  
  const anecdotal = anecdotes.anecdotes
  console.log(anecdotal, 'is anecdotal')
  const anecdotalSort = anecdotal.sort((a,b) => b.votes-a.votes)
  
  const filter = 'ALL'
  const anecFilter = useSelector(({ filter, anecFilter }) => {
    if (filter === 'ALL') {
      console.log('peepee')
      return
    }
    return console.log('poopoo')
  })
  
  const testAnec = anecdotal.filter((phrase) => phrase.content.toLowerCase().includes('x'))
  console.log(testAnec, 'is test anec')
  
  
  
  
  
  const dispatch = useDispatch()
  
  const vote = (id) => {
    dispatch(toAddVote(id))
  }
  
  
}

export default Anecdotes
