import { useContext } from 'react'
import AnexContext from '../anecdotesContext'


const Notification = () => {

   const [anex, dispatch] = useContext(AnexContext)
   console.log(anex, 'is anex in notif')
   
  const style = {
    border: 'solid',
    padding: 10,
    borderWidth: 1,
    marginBottom: 5
  }
  
  if (!anex) {
    return null
  } else {
      return (
        <div style={style}>
         {anex}
        </div>
      ) 
    setTimeout(()=> anex = '', 5000)
  }
}

export default Notification
