import { useQueryClient, useMutation } from 'react-query'
import { createAnec } from '../requests'
import { useReducer } from 'react'
import AnexContext from '../anecdotesContext'


const counterReducer = (state, action) => {
  switch (action.type) {
    case "FORM_SENT":
      console.log(JSON.parse(JSON.stringify(state)), 'is json json state in counterreducer')
      console.log(action, 'is action in counterreducer')
      return state
    default:
      return state
  }
}

const AnecdoteForm = () => {

  const queryClient = useQueryClient()
  
  const newAnecMutation = useMutation(createAnec, {
    onSuccess: (newAnec) => {
      const anex = queryClient.getQueryData('anecdotal')
      //console.log(anex, 'is anex in newmutate in anecform')
      queryClient.setQueryData('anecotal', anex.concat(newAnec))
    }    
  })

  const onCreate = (event) => {
    event.preventDefault()
    const content = event.target.anecdote.value
    event.target.anecdote.value = ''
    //console.log('new anecdote')
    newAnecMutation.mutate({ content })
  }
  
  const [counter, counterDispatch] = useReducer(counterReducer, 0)

  return (
    <AnexContext.Provider value={[counter]}>
    <div>
      <h3>create new</h3>
      <form onSubmit={onCreate}>
        <input 
          name='anecdote'
          placeholder='Enter a new anecdote...'
          />
        <button onClick={() => counterDispatch({type:"FORM_SENT", payload: "paparazzi"})}type="submit">create</button>
      </form>
    </div>
    </AnexContext.Provider>
  )
}

export default AnecdoteForm
