import { useQueryClient, useMutation } from 'react-query'
import { createAnec } from '../requests'
import { useContext } from 'react'
import AnexContext from '../anecdotesContext'

const AnecdoteForm = () => {

  const queryClient = useQueryClient()
  
  const newAnecMutation = useMutation(createAnec, {
    onSuccess: (newAnec) => {
      const anex = queryClient.getQueryData('anecdotal')
      console.log(anex, 'is anex in newmutate in anecform before post request')
      const catcher = queryClient.setQueryData('anecotal', anex.concat(newAnec))
      //console.log(catcher, 'is catcher in new mutate after post')
      //console.log(catcher[catcher.length-1], 'is catcher at last index in new mutate after post')
      if(typeof catcher[catcher.length-1] === 'undefined') {
        const newMsg = `entry is too short; it must be at least 5 characters`
        anexDispatch({ type: "FORM_SENT", payload: newMsg })
      } else {
        const newMsg = `you created the anecdote ${catcher[catcher.length-1].content}`
        //console.log(newMsg, 'is new msg')
        anexDispatch({ type: "FORM_SENT", payload: newMsg })    
      }
    }
  })
  
  const [anexxer, anexDispatch] = useContext(AnexContext)

  const onCreate = (event) => {
    event.preventDefault()
    const content = event.target.anecdote.value
    event.target.anecdote.value = ''
    //console.log('new anecdote')
    newAnecMutation.mutate({ content })
  }

  return (
    <div>
      <h3>create new</h3>
      <form onSubmit={onCreate}>
        <input 
          name='anecdote'
          placeholder='Enter a new anecdote...'
          />
        <button type="submit">create</button>
      </form>
    </div>
  )
}

export default AnecdoteForm
