import { useQueryClient, useMutation } from 'react-query'
import { createAnec } from '../requests'
import { useContext } from 'react'
import AnexContext from '../anecdotesContext'

//const anexReducer = (state, action) => {
//  switch (action.type) {
//    case "FORM_SENT":
//      //console.log(JSON.parse(JSON.stringify(state)), 'is json json state in anexreducer')
//      console.log(state, 'is state in anexreducer')
//      console.log(action, 'is action in anexreducer')
//      return action.payload
//  }
//}

const AnecdoteForm = () => {

  const queryClient = useQueryClient()
  
  const newAnecMutation = useMutation(createAnec, {
    onSuccess: (newAnec) => {
      const anex = queryClient.getQueryData('anecdotal')
      //console.log(anex, 'is anex in newmutate in anecform')
      const anexial = queryClient.setQueryData('anecotal', anex.concat(newAnec))
      console.log(anexial, 'is anexial in newanecmutate')
      if (anexial[anexial.length-1] === 'undefined'){
        console.log('we have an error in the undefined')
      }
    },
    onError: (error) => {
      console.log('we have an error')
    }    
  })
  
  const [anexxer, anexDispatch] = useContext(AnexContext)

  const onCreate = (event) => {
    event.preventDefault()
    const content = event.target.anecdote.value
    event.target.anecdote.value = ''
    //console.log('new anecdote')
    const newMsg = `you created the anecdote ${content}`
    console.log(newMsg, 'is new msg')
    newAnecMutation.mutate({ content })
    anexDispatch({ type: "FORM_SENT", payload: newMsg })
  }

  return (
    <div>
      <h3>create new</h3>
      <form onSubmit={onCreate}>
        <input 
          name='anecdote'
          placeholder='Enter a new anecdote...'
          />
        <button type="submit">create</button>
      </form>
    </div>
  )
}

export default AnecdoteForm
