import AnecdoteForm from './components/AnecdoteForm'
import Notification from './components/Notification'
import { useQuery, useMutation, useQueryClient } from 'react-query'
import { getAnec, createAnec, updateAnec } from './requests'

const App = () => {
  const queryClient = useQueryClient()
  
  const newAnecMutation = useMutation(createAnec, {
    onSuccess: (newAnec) => {
      const anex = queryClient.getQueryData('anecdotal')
      queryClient.setQueryData('anecotal', anecdotes.concat(newAnec))
    }    
  })
  
  const updateAnecMutation = useMutation(updateAnec, {
    onSuccess: () => {
      queryClient.invalidateQueries('anecdotal')
    }
  })

  const handleVote = (anecdote) => {
    //going to use mutation here
    console.log('vote')
  }

  const anecdotes = [
    {
      "content": "If it hurts, do it more often",
      "id": "47145",
      "votes": 0
    },
  ]
  
  const addAnec = async (event) => {
    event.preventDefault()
    const content = event.target.anecdote.value
    event.target.anecdote.value = ''
    newAnecMutation.mutate({ content })
  }
  
  const result = useQuery(
    'anecdotal', getAnec()
  )
  console.log(result, 'is result in app')
  
  if (result.isLoading) {
    return <div>loading data...</div>
  }
  
  const anecdotals = result.data

  return (
    <div>
      <h3>Anecdote app</h3>
    
      <Notification />
      <AnecdoteForm />
    
      {anecdotes.map(anecdote =>
        <div key={anecdote.id}>
          <div>
            {anecdote.content}
          </div>
          <div>
            has {anecdote.votes}
            <button onClick={() => handleVote(anecdote)}>vote</button>
          </div>
        </div>
      )}
    </div>
  )
}

export default App
